mapper.select:
select

from
t_ship_device
where
imo_number =#{imoNumber}
and
data_sync_time =#{dataSyncTime}
and
name =#{name}

select
  count(*)
from
  t_ship_device
where
  imo_number =#{imoNumber}
  and
  data_sync_time=#{dataSyncTime}
  and
  name =#{name}

SELECT d.id deviceId, d.collect_time dataSyncTime, IFNULL(AVG( m.zjjkll ) ,0) zjjkll, IFNULL(AVG( m.zjckll ) ,0) zjckll, IFNULL(AVG( m.fdjjkll ),0) fdjjkll, IFNULL(AVG( m.fdjckll ),0) fdjckll, IFNULL(AVG( m.glll ) ,0) glll, IFNULL(AVG( m.glckll ) ,0) glckll FROM t_ship_device d LEFT JOIN t_ship_device_mod m ON d.id = m.device_id WHERE
m.ship_id = #{shipId}
AND d.collect_time >= #{begin}
AND d.collect_time <= #{end}
GROUP BY d.id

select * from t_ship_device
AND ship_id = #{shipId}
order by collect_time desc limit 1

SELECT mefc, revolution_speed, cool_sea_w_press, mejip, mejit, generator_running_1, generator_running_2, generator_running_3, fip, fit, meloip, meloit, starting_air_pressure, control_air_pressure, air_main_fold_press, sloot, soet, siet, shaft_bearing_temp_inter, thrust_bearing_temp, shaft_bearing_temp_fore, shaft_bearing_temp_aft, gas_outlet_temp_1, gas_outlet_temp_2, gas_outlet_temp_3, alop1, alop2, alop3, alot1, alot2, alot3, collect_time FROM t_ship_device where ship_id = #{shipId}
AND collect_time >= #{begin}
AND collect_time <= #{end}
ORDER BY collect_time ASC

SELECT dt.ground_speed, dv.revolution_speed, dv.collect_time FROM t_ship_detail dt LEFT JOIN t_ship_device dv ON dt.collect_time = dv.collect_time
AND dt.ship_id = #{shipId} AND dv.ship_id = #{shipId}
AND dt.collect_time >= #{begin}
AND dt.collect_time <= #{end}
ORDER BY collect_time ASC

SELECT alop1, alop2, alop3, generator_running_1, generator_running_2, generator_running_3, collect_time FROM t_ship_device
alop1 is not null and alop2 is not null and alop3 is not null
AND ship_id = #{shipId}
AND collect_time >= #{begin}
AND collect_time <= #{end}
ORDER BY collect_time ASC

select
    *
from
    t_ship_device
where
    ship_id =#{shipId}
    AND collect_time >= #{begin}
    AND collect_time <= #{end}
order by
    collect_time
